			$c->setDbName(self::DATABASE_NAME);
		}

		FacturaPeer::addSelectColumns($c);
		$startcol2 = (FacturaPeer::NUM_COLUMNS - FacturaPeer::NUM_LAZY_LOAD_COLUMNS);

		ClientesPeer::addSelectColumns($c);
		$startcol3 = $startcol2 + (ClientesPeer::NUM_COLUMNS - ClientesPeer::NUM_LAZY_LOAD_COLUMNS);

		VendedorPeer::addSelectColumns($c);
		$startcol4 = $startcol3 + (VendedorPeer::NUM_COLUMNS - VendedorPeer::NUM_LAZY_LOAD_COLUMNS);

		CondicioPeer::addSelectColumns($c);
		$startcol5 = $startcol4 + (CondicioPeer::NUM_COLUMNS - CondicioPeer::NUM_LAZY_LOAD_COLUMNS);

				$c->addJoin(array(FacturaPeer::CO_CLI,), array(ClientesPeer::CO_CLI,), $join_behavior);
				$c->addJoin(array(FacturaPeer::CO_VEN,), array(VendedorPeer::CO_VEN,), $join_behavior);
				$c->addJoin(array(FacturaPeer::FORMA_PAG,), array(CondicioPeer::CO_COND,), $join_behavior);

		$stmt = BasePeer::doSelect($c, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = FacturaPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = FacturaPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$omClass = FacturaPeer::getOMClass();

				$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
				$obj1 = new $cls();
				$obj1->hydrate($row);
				FacturaPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

				// Add objects for joined Clientes rows

				$key2 = ClientesPeer::getPrimaryKeyHashFromRow($row, $startcol2);
				if ($key2 !== null) {
					$obj2 = ClientesPeer::getInstanceFromPool($key2);
					if (!$obj2) {
	
						$omClass = ClientesPeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					ClientesPeer::addInstanceToPool($obj2, $key2);
				} // if $obj2 already loaded

				// Add the $obj1 (Factura) to the collection in $obj2 (Clientes)
				$obj2->addFactura($obj1);

			} // if joined row is not null

				// Add objects for joined Vendedor rows

				$key3 = VendedorPeer::getPrimaryKeyHashFromRow($row, $startcol3);
				if ($key3 !== null) {
					$obj3 = VendedorPeer::getInstanceFromPool($key3);
					if (!$obj3) {
	
						$omClass = VendedorPeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					VendedorPeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (Factura) to the collection in $obj3 (Vendedor)
				$obj3->addFactura($obj1);

			} // if joined row is not null

				// Add objects for joined Condicio rows

				$key4 = CondicioPeer::getPrimaryKeyHashFromRow($row, $startcol4);
				if ($key4 !== null) {
					$obj4 = CondicioPeer::getInstanceFromPool($key4);
					if (!$obj4) {
	
						$omClass = CondicioPeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj4 = new $cls();
					$obj4->hydrate($row, $startcol4);
					CondicioPeer::addInstanceToPool($obj4, $key4);
				} // if $obj4 already loaded

				// Add the $obj1 (Factura) to the collection in $obj4 (Condicio)
				$obj4->addFactura($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


