    foreach (sfMixer::getCallables('BaseRengFacPeer:doSelectJoin:doSelectJoin') as $callable)
    {
      call_user_func($callable, 'BaseRengFacPeer', $c, $con);
    }


		$c = clone $c;

		// Set the correct dbName if it has not been overridden
		if ($c->getDbName() == Propel::getDefaultDB()) {
			$c->setDbName(self::DATABASE_NAME);
		}

		RengFacPeer::addSelectColumns($c);
		$startcol = (RengFacPeer::NUM_COLUMNS - RengFacPeer::NUM_LAZY_LOAD_COLUMNS);
		ArtPeer::addSelectColumns($c);

		$c->addJoin(array(RengFacPeer::CO_ART,), array(ArtPeer::CO_ART,), $join_behavior);
		$stmt = BasePeer::doSelect($c, $con);
