				$obj1->hydrate($row);
				RengFacPeer::addInstanceToPool($obj1, $key1);
			} // if $obj1 already loaded

			$key2 = ArtPeer::getPrimaryKeyHashFromRow($row, $startcol);
			if ($key2 !== null) {
				$obj2 = ArtPeer::getInstanceFromPool($key2);
				if (!$obj2) {

					$omClass = ArtPeer::getOMClass();

					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol);
					ArtPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 already loaded

				// Add the $obj1 (RengFac) to $obj2 (Art)
				$obj2->addRengFac($obj1);

