	/**
	 * Retrieve object using using composite pkey values.
	 * @param      int $fact_num
	   @param      int $reng_num
	   
	 * @param      PropelPDO $con
	 * @return     RengFac
	 */
	public static function retrieveByPK($fact_num, $reng_num, PropelPDO $con = null) {
		$key = serialize(array((string) $fact_num, (string) $reng_num));
 		if (null !== ($obj = RengFacPeer::getInstanceFromPool($key))) {
 			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(RengFacPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}
		$criteria = new Criteria(RengFacPeer::DATABASE_NAME);
		$criteria->add(RengFacPeer::FACT_NUM, $fact_num);
		$criteria->add(RengFacPeer::RENG_NUM, $reng_num);
		$v = RengFacPeer::doSelect($criteria, $con);

		return !empty($v) ? $v[0] : null;
	}
