	public function copyInto($copyObj, $deepCopy = false)
	{

		$copyObj->setCoSub($this->co_sub);

		$copyObj->setDesSub($this->des_sub);

		$copyObj->setCoAlma($this->co_alma);

		$copyObj->setCampo1($this->campo1);

